mapper.delete:
DELETE FROM branch_staff_tb 
WHERE staff_id=#{staff_id} 
AND staff_pw=#{staff_pw}

mapper.update:
UPDATE branch_staff_tb SET 
branch_owner_cd=#{branch_owner_cd}
, staff_name=#{staff_name} 
, staff_tel=#{staff_tel}
, staff_postcode=#{staff_postcode}
, staff_addr=#{staff_addr}
, staff_detail_addr=#{staff_detail_addr}
WHERE staff_id=#{staff_id} 
AND staff_pw=#{staff_pw}

mapper.insert:
INSERT INTO branch_staff_tb(
	staff_id
	, branch_owner_cd
	, staff_pw
	, staff_name
	, staff_tel
	, staff_postcode
	, staff_addr
	, staff_detail_addr
	, staff_in_date) 
VALUES(
	#{staff_id}
	, #{branch_owner_cd}
	, #{staff_pw}
	, #{staff_name}
	, #{staff_tel}
	, #{staff_postcode}
	, #{staff_addr}
	, #{staff_detail_addr}
	, now()
)
